import './ProductDetails.css';

import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState, useEffect } from 'react';

const API_URL = 'https://fakestoreapi.com/products';

function ProductDetails({ ProductId, SetCurrentPage }) {
    const [productData, setProductData] = useState("");

    const fetchProducts = async () => {

        const response = await fetch(API_URL + "/" + ProductId);
        const data = await response.json();

        setProductData(data);
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <div className='MainParent'>
            <br /><br /><br /><br />
            <button onClick={() => SetCurrentPage("Home")}>Go Back</button>
            <br />

            <img className='ProductImg' src={productData.image} style={{ width: 150 }}></img>
            <div className='AboutProduct'>
                <h3 style={{ fontWeight: 500 }}>{productData.title}</h3>
                <h1>${productData.price}</h1>

                <h4>Available offers</h4>

                <a style={{ fontWeight: 600 }}>Bank Offer </a> <a>5% Unlimited Cashback on Shopkart Credit Card <a href=''>T&C</a></a>
                <br /> <br />
                <a style={{ fontWeight: 600 }}>Bank Offer </a> <a>Flat 10% off on HDBR Bank Credit Card EMI Txns on 6 and 9 months tenure, Min. Txn Value: $50 <a href=''>T&C</a></a>
                <br /> <br />

                Bank OfferFlat ₹1,500 off on HDFC Bank Credit Card EMI Txns on 12 months tenure, Min. Txn Value: ₹15,000T&C

                Special PriceGet extra ₹6000 off (price inclusive of cashback/coupon)T&C

                {/* Need to check issue - with commented lines (ARRAY) */}

                {/* <h4>{productData.rating.rate}</h4>
            <h4> Total no. of ratings : {productData.rating.count}</h4> */}

                <br />

                <h3>Product Description</h3>
                <hr />
                <p className='Description'>
                    {productData.description}</p>

                <br />

                <h3>Product Category</h3>
                <hr />
                <a href='#' className='CategoryBody'>{productData.category}</a>

                <br /><br />

                <h3>Ratings & Reviews</h3>
                <hr />
                <p></p>
            </div>

        </div>
    )
}

export default ProductDetails;
