import LogoImg from './Images/Logo.png';
import MenuImg from './Images/menu.svg';
import './App.css';
import Banners from './Banners';

import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

const myStyles = {
  textAlign: 'center',
  marginTop: '2rem',
  backgroundColor: '#F43596',
};

function App() {

  return (
    <div className="App">

      <div className='Content'>

        <Carousel autoPlay={true} showStatus={false} showThumbs={false} style={myStyles}>
          <div className='CarouselItem'>
            <img src={Banners[0]}></img>
          </div>
          <div className='CarouselItem'>
            <img src={Banners[1]}></img>
          </div>
          <div className='CarouselItem'>
            <img src={Banners[2]}></img>
          </div>
        </Carousel>

      </div >

      <div className='TopBar'>
        <button className='Menu'><img src={MenuImg} /></button>
        <img className='Logo' src={LogoImg}></img> <p className='CompanyName'>Shopkart</p>
        <input className='SearchBar' placeholder='Search Products'></input>
        <button className='LoginBtn'>Sign in</button>

        <button className='TabBtns'>Explore</button>
        <button className='TabBtns'>Sell</button>
        <button className='TabBtns'>Cart</button>
      </div>
    </div >
  );
}

export default App;
