import LogoImg from './Images/Logo.png';
import MenuImg from './Images/menu.svg';
import Banners from './Banners';

import './App.css';
import { useState, useEffect } from 'react';
import React, { Component } from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

// Carousel Btns - Custom Component
import CarouselBtns from './Components/CarouselBtns/CarouselBtns.js';

// Product Details
import ProductDetails from './Components/ProductPage/ProductDetails.js';

// ----------------------------------------------------------------//

// API url ->
const API_URL = 'https://fakestoreapi.com/products';

// Pages
const Pages = ['Home', 'ProductPage'];

function App() {
  const [productData, setProductData] = useState("");

  const fetchProducts = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();

    setProductData(data);
  }

  const [CurrentPage, setCurrentPage] = useState(Pages[0]);

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="App">

      {/* Pages */}
      {
        CurrentPage == "Home" ?
          <HomePage productData={productData} CurrentPage={setCurrentPage} />
          :
          <h1>{ProductSelected_ID}</h1>
        // <ProductDetails />
      }

      <TopBar />
    </div >
  );
}

// Home Page -
function HomePage({ productData, CurrentPage }) {
  return (
    <div className='Content'>

      <div className='CarouselParent'>
        <Carousel autoPlay={true} showStatus={false} showThumbs={false} infiniteLoop={true} renderArrowNext={CarouselBtns[0]} renderArrowPrev={CarouselBtns[1]}>
          <div className='CarouselItem'>
            <img src={Banners[0]}></img>
          </div>
          <div className='CarouselItem'>
            <img src={Banners[1]}></img>
          </div>
          <div className='CarouselItem'>
            <img src={Banners[2]}></img>
          </div>
        </Carousel>
      </div>

      <ProductList Data={productData} CurrentPage={CurrentPage} />
      <ProductList Data={productData} Category='electronics' CurrentPage={CurrentPage} />
      <ProductList Data={productData} Category='jewelery' CurrentPage={CurrentPage} />
    </div >
  )
}
function TopBar() {
  return (
    <div className='TopBar'>
      <button className='Menu'><img src={MenuImg} /></button>
      <img className='Logo' src={LogoImg}></img> <p className='CompanyName'>Shopkart</p>
      <input className='SearchBar' placeholder='Search Products'></input>
      <button className='LoginBtn'>Sign In</button>

      <button className='TabBtns'>Explore</button>
      <button className='TabBtns'>Sell</button>
      <button className='TabBtns'>Cart</button>
    </div>
  )
}

function ProductList({ Data, Category = "", CurrentPage }) {
  return (
    <div className='ProductListParent'>
      {Category != "" ?
        <h2 className='PL_Heading'>{Category}</h2> :
        <h2>Best seller</h2>}

      <div className='ProductList'>

        {Data?.length > 0 ? (
          <>
            {
              Data.map((data) =>
                (<Product ProductData={data} ProductCategory={Category} CurrentPage={CurrentPage} />))
            }
          </>)
          : (
            <div className='empty'>
              <h2>No products found</h2>
            </div>)}

      </div>
    </div>
  )
}

function Product({ ProductData, ProductCategory = "", CurrentPage }) {
  return (

    // Check if product info will be shown or not
    // Based on category
    <>
      {
        ProductCategory == "" ?
          (
            <ProductInfo ProductData={ProductData} CurrentPage={CurrentPage} />
          )
          :
          ProductData.category == ProductCategory ?
            (
              <ProductInfo ProductData={ProductData} CurrentPage={CurrentPage} />
            )
            : <></>
      }
    </>
  )
}

function ProductInfo({ ProductData, CurrentPage }) {

  function OnProductClick(ProductId) {
    ProductSelected_ID = ProductId;
    CurrentPage(Pages[1]);
  }

  return (
    <div className='Product' onClick={() => OnProductClick(ProductData.id)}>
      <div className='ProductImgContainer'>
        <img src={ProductData.image}></img>
      </div>
      <p className='ProductTitle'>{ProductData.title}</p>
      <h5 className='ProductPrice'>${ProductData.price}</h5>
    </div>
  )
}

// User Data
var ProductSelected_ID = 0;

export default App;
