import './ProductDetails.css';

import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState, useEffect } from 'react';

const API_URL = 'https://fakestoreapi.com/products';

function ProductDetails({ ProductId, SetCurrentPage }) {
    const [productData, setProductData] = useState("");

    const fetchProducts = async () => {

        const response = await fetch(API_URL + "/" + ProductId);
        const data = await response.json();

        setProductData(data);
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <div className='MainParent'>
            <br /><br /><br /><br />
            <button onClick={() => SetCurrentPage("Home")}>Go Back</button>
            <br />

            <img className='ProductImg' src={productData.image} style={{ width: 150 }}></img>
            <div className='AboutProduct'>
                <h3 style={{ fontWeight: 600 }}>{productData.title}</h3>
                <h1>${productData.price}</h1>

                {/* Need to check issue - with commented lines (ARRAY) */}

                {/* <h4>{productData.rating.rate}</h4>
            <h4> Total no. of ratings : {productData.rating.count}</h4> */}

                <br />

                <h4>{productData.category}</h4>

                <p className='Description'>
                    {productData.description}</p>
            </div>

        </div>
    )
}

export default ProductDetails;
